        /* Create and display the form */
        LoadingFrame ld = new LoadingFrame();
        java.awt.EventQueue.invokeLater(new Runnable() {
            public void run() {
                ld.setVisible(true);
            }
        });
        try {
            for (int i = 0; i <= 100; i++) {
                Thread.sleep(20);
                ld.LoadingValue.setText(i + "%");
                if (i == 10) {
                    ld.LoadingText.setText("Loading...");
                }
                if (i == 30) {
                    ld.LoadingText.setText("Connecting...");
                }
                if (i == 50) {
                    ld.LoadingText.setText("Successfully connected...");
                }
                if (i == 70) {
                    ld.LoadingText.setText("Enjoy the Show...");
                }
                ld.jProgressBar1.setValue(i);
            }
            if (ld.jProgressBar1.getValue() == 100) {
                LoginFrame login = new LoginFrame();
                login.setVisible(true);
                ld.dispose();
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
